const DiscordBots = require("./base/DiscordBots");
const DiscordBotsList = require("./base/DiscordBotsList");

module.exports = class Client {

	createDiscordBotsClient(options) {
		return new DiscordBots({
			token : options.token,
			clientID : options.clientID
		})
	}

	createDiscordBotsListClient(options) {
		return new DiscordBotsList({
			token : options.token,
			clientID : options.clientID
		})
	}

}